function Get-OriginalUrl {     param (         [string]$linkProtectUrl     )     # Extract the original URL     if ($linkProtectUrl -match 'a=([^&]+)') {         $originalUrl = $matches[1]         # Decode the URL using Uri class         $decodedUrl = [System.Uri]::UnescapeDataString($originalUrl)         return $decodedUrl     } else {         Write-Error "Invalid Barracuda LinkProtect URL"     } } # Example usage $linkProtectUrl = Read-Host -Prompt "Paste the URL you want to decode." $originalUrl = Get-OriginalUrl -linkProtectUrl $linkProtectUrl Write-Output "Original URL: $originalUrl"  